sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
}

sp_widget.script:
(function($location) {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.isReturnMail = isReturnMailVisible();
	data.isIntelligentDelivery = isIntelligentDeliveryVisible();
	data.isPrintPlatformVisible = isPrintPlatformVisible();
	data.isBusinessAnalyticsVisible = isBusinessAnalyticsVisible();

	console.log("1:" + data.isReturnMail);
	console.log("2:" + data.isIntelligentDelivery);
	console.log("3:" + data.isPrintPlatformVisible);
	console.log("4:" + data.isBusinessAnalyticsVisible);

	function returnMailRedirect()
	{
		alert("hello world");

		$location.url("www.google.com");
	}


	function isReturnMailVisible ()
	{ 
		if(gs.getUser().hasRole("itil")){
			return true;
		}

		var gr = new GlideRecord("u_usp_urls");
		gr.addActiveQuery();
		gr.addEncodedQuery("u_customer=" + gs.getUser().getCompanyID() );
    gr.query();
		while(gr.next())
			{
				if(gr.u_product.category.sys_name == "RMAAS")
					{
					  return true;
					}
			}

		return false;
	}

	function isIntelligentDeliveryVisible(){
		if(gs.getUser().hasRole("itil")){
			return true;
		}

		var gr = new GlideRecord("u_usp_urls");
		gr.addActiveQuery();
		gr.addEncodedQuery("u_customer=" + gs.getUser().getCompanyID() );
    gr.query();
		while(gr.next())
			{
				if(gr.u_product.category.sys_name == "PPCS")
					{
					  return true;
					}
			}

		return false;
	}

	function isPrintPlatformVisible(){
		if(gs.getUser().hasRole("itil")){
			return true;
		}
		return false;
	}

	function isBusinessAnalyticsVisible(){
		if(gs.getUser().hasRole("itil")){
			return true;
		}
		return false;

	}


})();
